import React, { useEffect, useState } from "react";
import axios from "axios";

const API = "http://localhost:4000";

export default function Marketplace({ token }) {
  const [nfts, setNfts] = useState([]);

  useEffect(() => {
    axios.get(`${API}/nft`).then(res => setNfts(res.data));
  }, []);

  const buy = async (id, currency) => {
    await axios.post(`${API}/nft/buy`, { nftId: id, currency }, {
      headers: { Authorization: `Bearer ${token}` }
    });
    alert("✅ NFT bought!");
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-3">NFT Marketplace</h2>
      {nfts.map(n => (
        <div key={n.id} className="border p-3 mb-2 rounded">
          <h3 className="font-semibold">{n.name} ({n.category})</h3>
          <p>Price: {n.priceUsdt} USDT | {n.priceTon} TON</p>
          <p>Weekly Reward: {n.weeklyPercent}%</p>
          <button onClick={() => buy(n.id, "USDT")} className="mr-2 bg-green-600 text-white px-2 py-1 rounded">Buy USDT</button>
          <button onClick={() => buy(n.id, "TON")} className="bg-blue-600 text-white px-2 py-1 rounded">Buy TON</button>
        </div>
      ))}
    </div>
  );
}
