import React, { useEffect, useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import axios from "axios";
import Navbar from "./components/Navbar";
import Login from "./pages/Login";
import Dashboard from "./pages/Dashboard";
import Marketplace from "./pages/Marketplace";
import Deposit from "./pages/Deposit";
import Withdraw from "./pages/Withdraw";
import AdminPanel from "./pages/AdminPanel";
import telegramAuth from "./telegram/telegram";

const API_BASE = import.meta.env.VITE_API_BASE || "http://localhost:4000/api";

export default function App() {
  const [me, setMe] = useState(null);
  const [token, setToken] = useState(null);

  useEffect(() => {
    // Telegram WebApp init (if inside Telegram). For dev fallback returns debug user.
    const tg = telegramAuth();
    if (tg) {
      axios.post(`${API_BASE}/auth/login`, {
        telegramId: tg.id,
        username: tg.username,
        firstName: tg.first_name,
        lastName: tg.last_name
      }).then(res => {
        setToken(res.data.token);
        setMe(res.data.user);
      }).catch(err => {
        console.error("Auth error:", err.response?.data || err.message);
      });
    }
  }, []);

  return (
    <div className="min-h-screen bg-cyber text-white">
      <Navbar me={me} />
      <div className="max-w-7xl mx-auto p-6">
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/dashboard" element={me ? <Dashboard me={me} token={token} /> : <Navigate to="/login" />} />
          <Route path="/market" element={<Marketplace me={me} token={token} />} />
          <Route path="/deposit" element={<Deposit me={me} token={token} />} />
          <Route path="/withdraw" element={<Withdraw me={me} token={token} />} />
          <Route path="/admin" element={<AdminPanel token={token} />} />
          <Route path="/" element={<Navigate to="/dashboard" />} />
        </Routes>
      </div>
    </div>
  );
}
