import React, { useState } from "react";
import axios from "axios";

const API = "http://localhost:4000";

export default function Deposit({ me }) {
  const [amount, setAmount] = useState("");
  const [invoice, setInvoice] = useState(null);

  const createInvoice = async () => {
    const res = await axios.post(`${API}/payment/create-deposit`, {
      userId: me.id,
      amountTon: amount
    });
    setInvoice(res.data.invoice);
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-2">Deposit TON</h2>
      <input value={amount} onChange={e => setAmount(e.target.value)} className="border p-1 mr-2" placeholder="Amount in TON" />
      <button onClick={createInvoice} className="bg-blue-600 text-white px-3 py-1 rounded">Create</button>
      {invoice && (
        <div className="mt-3">
          <p>Send exactly <b>{invoice.amountTon} TON</b> to:</p>
          <p className="break-all">{invoice.address}</p>
        </div>
      )}
    </div>
  );
}
