import Card from "../components/Card";
import { motion } from "framer-motion";
import axios from "axios";

const API_BASE = import.meta.env.VITE_API_BASE || "http://localhost:4000/api";

export default function Dashboard({ me }) {
  const [nfts, setNfts] = React.useState([]);

  React.useEffect(() => {
    axios.get(`${API_BASE}/nft`).then(r => setNfts(r.data)).catch(e => console.error(e));
  }, []);

  return (
    <>
      <motion.h1 initial={{ x: -20 }} animate={{ x: 0 }} className="text-4xl neon-text mb-6">Welcome, {me?.username || me?.firstName}</motion.h1>
      <div className="grid grid-cols-3 gap-6">
        <Card>
          <h3 className="text-lg font-bold text-pink-300">ðŸ’° Balances</h3>
          <p>TÐžN: {me?.balanceTon ?? 0}</p>
          <p>USDT: {me?.balanceUsdt ?? 0}</p>
        </Card>
        <Card>
          <h3 className="text-lg font-bold text-cyan-300">ðŸ“ˆ My Portfolio</h3>
          <p>Owned NFTs: --</p>
        </Card>
        <Card>
          <h3 className="text-lg font-bold text-yellow-300">ðŸ“… Weekly Rewards</h3>
          <p>Next payout: Monday 00:00 UTC</p>
        </Card>
      </div>

      <h2 className="text-2xl mt-8 mb-4 neon-text">Latest NFTs</h2>
      <div className="grid grid-cols-3 gap-6">
        {nfts.map(n => (
          <Card key={n._id}>
            <img src={n.imageUrl || "https://via.placeholder.com/300"} alt={n.name} className="w-full h-40 object-cover rounded mb-3" />
            <h3 className="font-bold">{n.name}</h3>
            <p className="text-sm text-gray-300">{n.category}</p>
            <p className="mt-2">Price: {n.priceUsdt} USDT | {n.priceTon} TON</p>
          </Card>
        ))}
      </div>
    </>
  );
}
